import React, { useState } from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
  Col,
  Row,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";


const FirstPage = ({ onNextStep }) => {

  const [values, setValues] = useState({
    photo: "",
    email: "",
    firstName: "",
    lastName: "",
    experience: "",
    password: "",
    company: "",
  });

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form values:", values);
    // Perform necessary actions like submitting the form data
    onNextStep();
  };

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen((prevState) => !prevState);



  return(
    <Form onSubmit={handleSubmit}>

      <Alert color="info">
        <strong>Lovely to see you!</strong><br/> Filling out the form only takes a couple of minutes.
        We&apos;d love to learn more about your background and the ins-and-outs
        of why you&apos;d like to become a mentor. Keep things personal and talk
        directly to us and your mentees. We don&apos;t need jargon and polished
        cover letters here! <br/><br/>You agree to our code of conduct and the mentor
        agreement by sending the form, so be sure to have a look at those.
      </Alert>

      <FormGroup>
      {/* <span>
                <img alt="Profile" src="/assets/img/profiles/l-1.jpg" />
              </span> */}
        <Label for="photo">Photo</Label>
        <Input type="file" name="photo" id="photo" />
      </FormGroup>

      <Row>
        <Col md={6}>
          <FormGroup>
            <Label for="firstName">First Name*</Label>
            <Input
              id="firstName"
              name="firstNAme"
              // placeholder="with a placeholder"
              type="text"
            />
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>
            <Label for="lastName">Last Name*</Label>
            <Input
              id="lastName"
              name="lastName"
              // placeholder="password placeholder"
              type="text"
            />
          </FormGroup>
        </Col>
      </Row>
      <Row>
        <Col md={6}>
          <FormGroup>
            <Label for="email">Email*</Label>
            <Input type="email" name="email" id="email" required />
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>
            <Label for="password">Choose Password*</Label>
            <Input type="password" name="password" id="password" required />
          </FormGroup>
        </Col>
      </Row>

      <Row>
        <Col md={6}>
      <FormGroup>
        <Label for="jobTitle">Job Title*</Label>
        <Input type="text" name="jobTitle" id="jobTitle" required />
      </FormGroup>
       </Col>
       <Col md={6}>

      <FormGroup>
        <Label for="company">Company*</Label>
        <Input type="text" name="company" id="company" required />
      </FormGroup>
      </Col>
      </Row>
      <FormGroup>
      <Label for="company">Location*</Label>

      <Dropdown isOpen={dropdownOpen} toggle={toggle} >
        <DropdownToggle caret>Select location</DropdownToggle>
        <DropdownMenu >
          <DropdownItem header>Location</DropdownItem>
          <DropdownItem>Location 1</DropdownItem>
          <DropdownItem >Location 1</DropdownItem>
          <DropdownItem >Location 2</DropdownItem>
          <DropdownItem>Location 3</DropdownItem>
          <DropdownItem>Location 4</DropdownItem>
          <DropdownItem>Location 5</DropdownItem>
        </DropdownMenu>
      </Dropdown>
      </FormGroup>
<div className="d-flex justify-content-end">
      <Button type="submit" color="info" >
        Next step
      </Button>
      </div>
    </Form>
  );
};


const SecondPage = ({ onNextStep }) => {
  return(
    <div>
    <h1>Second page</h1>
    <div className="d-flex justify-content-end">
      <Button type="submit" color="info" onClick={onNextStep}>
        Next step
      </Button>
      </div>
    </div>
  );
};
const ThirdPage = ({ onSubmit }) => {
  return(
    <div>
    <h1>Third page</h1>

      <div className="d-flex justify-content-end">
      <Button type="submit" color="info" onClick={onSubmit}>
        Submit
      </Button>
      </div>
    </div>
  );
};


const Mylogin = () => {
  const [step, setStep] = useState(1);

  const handleNextStep = () => {
    setStep(step + 1);
  };
  return (
    <Form>
      <h1>Apply as a mentor</h1>
      {step === 1 && <FirstPage onNextStep={handleNextStep} />}
      {step === 2 && <SecondPage onNextStep={handleNextStep} />}
      {step === 3 && <ThirdPage onSubmit={() => console.log("Form submitted")} />}
    </Form>
  );
};

export default Mylogin;
