const BottomNavigation = ({
  className,
  onClickPrev,
  prevLabel,
  onClickNext,
  // nextLabel,
}) => {
  return (
    <WithWizard
      render={({ next, previous, step, steps }) => {
        if (steps.indexOf(step) === 3) {
          return null;
        }

        return (
          <div className={`wizard-buttons ${className}`}>
            {steps.indexOf(step) > 0 && (
              <Button
                color="primary"
                outline
                className={`mr-1 ${steps.indexOf(step) <= 0 ? "disabled" : ""}`}
                onClick={() => {
                  onClickPrev(previous, steps, step);
                }}
              >
                {prevLabel}
              </Button>
            )}
            <div>
              <Button
                color="primary"
                className={
                  steps.indexOf(step) >= steps.length - 1 ? "disabled" : ""
                }
                onClick={() => {
                  onClickNext(next, steps, step);
                }}
              >
                {/* {nextLabel} */}
                {steps.indexOf(step) === 2 ? "Submit Application" : "Next Step"}
              </Button>
            </div>
          </div>
        );
      }}
    />
  );
};

