import React, { useState } from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
  Col,
  Row,
  CustomInput,
  // InputGroup,
  // InputGroupAddon,
  FormText,
  CardBody,
  Card,
  Jumbotron,
} from "reactstrap";
import ThumbnailImage from "components/cards/ThumbnailImage";
import { AvField, AvForm} from "availity-reactstrap-validation";
import { Wizard, Steps, Step, WithWizard } from 'react-albus';
import { injectIntl } from 'react-intl';
// import IntlMessages from 'helpers/IntlMessages';
// import BottomNavigation from 'components/wizard/BottomNavigation';
import TopNavigation from 'components/wizard/TopNavigation';
// import IntlMessages from 'helpers/IntlMessages';
import { Colxx } from 'components/common/CustomBootstrap';
// import Breadcrumb from 'containers/navs/Breadcrumb';



const AboutYou = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onloadend = () => {
        setSelectedFile(reader.result);
      };

      reader.readAsDataURL(file);
    }
  };

  const LocationData = [
    "Location1",
    "Location2",
    "Location3",
    "Location4",
  ]


  return (
    <Form>
      <Alert color="primary">
        <strong>Lovely to see you!</strong>
        <br /> Filling out the form only takes a couple of minutes. We&apos;d
        love to learn more about your background and the ins-and-outs of why
        you&apos;d like to become a mentor. Keep things personal and talk
        directly to us and your mentees. We don&apos;t need jargon and polished
        cover letters here! <br />
        <br />
        You agree to our code of conduct and the mentor agreement by sending the
        form, so be sure to have a look at those.
      </Alert>

      <FormGroup>
        <Label for="photo">Photo</Label>
        <Row>
          <Col md={1}>
            <ThumbnailImage
              rounded
              small
              src={
                selectedFile ||
                "https://gogo-react.coloredstrategies.com/assets/img/profiles/l-1.jpg"
              }
              alt="photo"
              // className="m-1"
            />
          </Col>
          <Col md={2} className="mt-3">
            <Label for="photo" className="d-md-none">
              Photo
            </Label>
            {/* <Input type="file" name="photo" id="photo" accept=".jpg, .jpeg, .png" 
       onChange={handleFileChange} required /> */}
            {/* <InputGroup> */}
            {/* <CustomInput
              type="file"
              id="exampleCustomFileBrowser4"
              name="customFile"
              accept=".jpg, .jpeg, .png"
              onChange={handleFileChange}
              required
              className="w-150"
            /> */}
            {/* <InputGroup className="mb-3"> */}
                {/* <InputGroupAddon addonType="prepend">Upload</InputGroupAddon> */}
                <CustomInput
                  type="file"
                  id="exampleCustomFileBrowser1"
                  name="customFile"
              onChange={handleFileChange}

                />
              {/* </InputGroup> */}

            {/* </InputGroup> */}
          </Col>
        </Row>
      </FormGroup>

      <Row>
        <Col md={6}>
          <FormGroup>
            <Label for="firstName">First Name*</Label>
            <Input id="firstName" name="firstNAme" type="text" required />
            {/* <AvField name="firstname" label="First Name*" required /> */}
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>
            <Label for="lastName">Last Name*</Label>
            <Input id="lastName" name="lastName" type="text" required />
            {/* <AvField name="lastname" label="Last Name*" required /> */}
          </FormGroup>
        </Col>
      </Row>
      <Row>
        <Col md={6}>
          <FormGroup>
            <Label for="email">Email*</Label>
            <Input type="email" name="email" id="email" required />
            {/* <AvField name="email" label="Email*" type="email" required /> */}
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>
            <Label for="password">Choose a Password*</Label>
            <Input type="password" name="password" id="password" required />
            {/* <AvField
              name="Password*"
              label="Choose a Password*"
              type="password"
              required
            /> */}
          </FormGroup>
        </Col>
      </Row>

      <Row>
        <Col md={6}>
          <FormGroup>
            <Label for="jobTitle">Job Title*</Label>
            <Input type="text" name="jobTitle" id="jobTitle" required />
            {/* <AvField name="jobTitle" label="Job Title*" required /> */}
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>
            <Label for="company">Company</Label>
            <Input type="text" name="company" id="company" />
            {/* <AvField name="company" label="Company" /> */}
          </FormGroup>
        </Col>
      </Row>
      <AvForm>
        {/* <Col md={6}> */}
        {/* <Label for="Location">Location</Label> */}

        <AvField
          type="select"
          name="location"
          label="location*"
          required
          className="form-control-sm"
        >
          {/* <option> Select Location</option>
          <option>Location1</option>
          <option>Location2</option>
          <option>Location3</option>
          <option>Location4</option> */}

          {LocationData.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
        </AvField>

        {/* <Input type="select" name="category" id="category">
            <option>Select location</option>
            <option>Location2</option>
            <option>Location3</option>
            <option>Location4</option>
          </Input> */}
        {/* </Col> */}
      </AvForm>
      {/* <div className="d-flex justify-content-end">
        <Button type="submit" color="primary" onClick={onNextStep}>
          Next step
        </Button>
      </div> */}
    </Form>
  );
};

const Profile = () => {

  const CategoryData = [
    "Category1",
    "Category2",
    "Category3",
    "Category4",
  ]

  return (
    <Form>
      <AvForm>
        {/* <Col md={6}>
          <Label for="category">Category*</Label>
          <Input type="select" name="category" id="category" required>
            <option>Select Category</option>
            <option>Category2</option>
            <option>Category1</option>
            <option>Category3</option>
            <option>Category4</option>
          </Input>
        </Col> */}
        <AvField
          type="select"
          name="category"
          label="Category*"
          required
          className="form-control-sm"
        >
          {/* <option> Select Category</option>
          <option>Category2</option>
          <option>Category1</option>
          <option>Category3</option>
          <option>Category4</option> */}

          {CategoryData.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
        </AvField>

      
      </AvForm>
      <FormGroup>
        <Label for="skill">Skills*</Label>
        <Input
          type="text"
          name="skill"
          id="skill"
          placeholder="Add a new skill..."
          required
        />
        {/* <AvField
          name="skill"
          label="Skill*"
          required
          placeholder="Add a new skill..."
        /> */}

        <FormText color="muted">
          Describe your expertise to connect with mentees who have similar
          interests.
          <br />
          Comma-separated list of your skills (keep it below 10). Mentees will
          use this to find you.
        </FormText>
      </FormGroup>
      <FormGroup>
        <Label for="bio">Bio*</Label>
        <Input type="textarea" name="bio" id="bio" required />
        {/* <Label>Bio*</Label> */}

        {/* <AvInput type="textarea" name="bio" id="bio" required /> */}
        {/* <AvFeedback>Please enter some details!</AvFeedback> */}

        <FormText color="muted">
          Tell us (and your mentees) a little bit about yourself. Talk about
          yourself in the first person, as if you&apos;d directly talk to a
          mentee. This will be public.
        </FormText>
      </FormGroup>
      <FormGroup>
        <Row>
          <Col md={6}>
            <Label for="linkedinUrl">LinkedIn URL*</Label>
            <Input type="url" name="linkedinUrl" id="linkedinUrl" required />
            {/* <AvField name="linkedinUrl" label="LinkedIn URL*" required /> */}
          </Col>
          <Col md={6}>
            <Label for="twitterHandle">Twitter Handle (optional)</Label>
            <Input type="text" name="twitterHandle" id="twitterHandle" />
            {/* <AvField
              name="twitterHandle"
              label="Twitter Handle (optional)"
              required
            /> */}

            <FormText color="muted">
              Omit the &ldquo;@&rdquo; -e.g. &ldquo;dqmonn&rdquo;
            </FormText>
          </Col>
        </Row>
      </FormGroup>
      <FormGroup>
        <Label for="personalWebsite">Personal Website (optional)</Label>
        <Input type="url" name="personalWebsite" id="personalWebsite" />
        {/* <AvField
          name="twitterHandle"
          label="Twitter Handle (optional)"
          required
        /> */}

        <FormText color="muted">
          You can add your blog, GitHub profile or similar here
        </FormText>
      </FormGroup>
      {/* <Row>
        <Col xs={6}>
          <Button
            outline
            color="secondary"
            className="mb-2"
            onClick={onPreStep}
          >
            Previous step
          </Button>
        </Col>
        <Col xs={6} className="d-flex justify-content-end mb-2">
          <Button color="primary" type="submit" onClick={onNextStep}>
            Next step
          </Button>
        </Col>
      </Row> */}
    </Form>
  );
};
const Experience = () => {
  return (
    <div>
      <Alert color="primary">
        <strong>Almost there!</strong> <br /> You&apos;re just one last step
        away from being a mentor and connecting with mentees all over the world!
        in this step, shows off your accomplishments and how you can help
        others.
        <br />
        <br /> Many of these fields are optional, but will help us get better
        insights into your work - and therefore exponentially increase your
        chances. They also give you a jumpstart once you&apos;re a mentor.
      </Alert>

      <FormGroup>
        <Row>
          <Col md={6}>
            <Label for="introvideo">Intro Video</Label>
            <Input
              type="url"
              name="introvideo"
              id="introvideo"
              placeholder="https://your-intro-video-URL"
            />
            <FormText color="muted">
              Add a youTube video or record a Loom for your future mentees
            </FormText>
          </Col>

          <Col md={6}>
            <Label for="featuredarticle">Featured Article</Label>
            <Input
              type="url"
              name="featuredarticle"
              id="featuredarticle"
              placeholder="https://your-blog-article-URL"
            />
            <FormText color="muted">
              Link an interview / podcast /piece of writing you are proud of or
              were featured in.
            </FormText>
          </Col>
        </Row>
      </FormGroup>
      <FormGroup>
        <Label>
          Why do you want to become a mentor?(Not publicaly visible)*
        </Label>
        <Input type="textarea" name="why" id="why" />
      </FormGroup>
      <FormGroup>
        <Label>
          What, in your opinion, has been your greatest achievement so far?(Not
          publicaly visible)*
        </Label>
        <Input type="textarea" name="what" id="what" />
      </FormGroup>

      {/* <Row>
        <Col xs="6">
          <Button
            outline
            color="secondary"
            className="mb-2"
            onClick={onPreStep}
          >
            Previous step
          </Button>
        </Col>
        <Col xs="6" className="d-flex justify-content-end mb-2">
          <Button type="submit" color="primary">
            Submit application
          </Button>
        </Col>
      </Row> */}
    </div>
  );
};



const JumbotronUi = () => {
  return (
    <>
      {/* <Row>
        <Colxx xxs="12">
          <Breadcrumb heading="head" />
          <Separator className="mb-5" />
        </Colxx>
      </Row> */}
      <Row>
        <Colxx xxs="12" className="mb-4">
          <Card>
            <CardBody className="text-center">
              <i alt="" className="glyph-icon iconsminds-yes"  style={{ fontSize: "75px", color: "green" }} />
              
              <Jumbotron className="text-center">

                <h1 className="display-4">
                  Submitted Succesfully!
                  {/* <IntlMessages id="jumbotron.hello-world" /> */}
                </h1>
                <p className="lead">
                  We will reach you shortly
                  {/* <IntlMessages id="jumbotron.lead" /> */}
                </p>
                <hr className="my-4" />
                {/* <p>
                  check 2
                  <IntlMessages id="jumbotron.lead-detail" />
                </p> */}
                <p className="lead mb-0 ">
                  <Button color="primary" size="lg">
                    check status
                    {/* <IntlMessages id="jumbotron.learn-more" /> */}
                  </Button>
                </p>
              </Jumbotron>
            </CardBody>
          </Card>
        </Colxx>
      </Row>
    </>
  );
};


const BottomNavigation = ({
  className,
  onClickPrev,
  prevLabel,
  onClickNext,
  nextLabel,
}) => {
  return (
    <WithWizard
      render={({ next, previous, step, steps }) => (
        <div className={`wizard-buttons ${className} ${steps.indexOf(step) === 1 ? 'hide-buttons' : ''}`}>
          <Button
            color="primary"
            outline
            className={`mr-1 ${steps.indexOf(step) <= 0 ? 'disabled' : ''}`}
            onClick={() => {
              onClickPrev(previous, steps, step);
            }}
          >
            {prevLabel}
          </Button>

          <Button
            color="primary"
            className={
              steps.indexOf(step) >= steps.length - 1 ? 'disabled' : ''
            }
            onClick={() => {
              onClickNext(next, steps, step);
            }}
          >
            {nextLabel}
          </Button>
        </div>
      )}
    />
  );
};


// const Mylogin = () => {
//   const [step, setStep] = useState(1);

//   const handleNextStep = () => {
//     setStep(step + 1);
//   };
//   const handlePreviousStep = () => {
//     setStep(step - 1);
//   };
//   return (
//     <Form className="mx-auto my-4" style={{ maxWidth: "900px" }}>
//       <h1 className="font-weight-bold">Apply as a mentor</h1>
//       {step === 1 && <AboutYou onNextStep={handleNextStep} />}
//       {step === 2 && (
//         <Profile onNextStep={handleNextStep} onPreStep={handlePreviousStep} />
//       )}
//       {step === 3 && <Experience onPreStep={handlePreviousStep} />}
//     </Form>
//   );
// };

// export default Mylogin;



const Mylogin = () => {
  // const [name, setName] = useState('');
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');

  const topNavClick = (stepItem, push) => {
    push(stepItem.id);
  };

  const onClickNext = (goToNext, steps, step) => {
    const updatedStep = { ...step, isDone: true };
    if (steps.length - 1 <= steps.indexOf(updatedStep)) {
        return;
    }
    goToNext();
 };

  const onClickPrev = (goToPrev, steps, step) => {
    if (steps.indexOf(step) <= 0) {
      return;
    }
    goToPrev();
  };

  // const { messages } = intl;
  return (
    <Card className="mx-auto my-4 " style={{ maxWidth: "900px" }}>
      <CardBody className="wizard wizard-default">
        <h1 className="mt-4 font-weight-bold">Apply as a mentor</h1>
        <Wizard>
          <TopNavigation
            className="justify-content-center"
            disableNav={false}
            topNavClick={topNavClick}
           onClickNext={onClickNext}
           onClickPrev={onClickPrev}
          />
          <Steps>
            <Step
              id="step1"
              name="About You"
            >
                
                <AboutYou />
            </Step>
            <Step
              id="step2"
              name="Profile"
            >
                <Profile/>
            </Step>
            <Step
              id="step3"
              name="Experience"
            >
              
                <Experience/>

            </Step>
            <Step id="step4" hideTopNav className="justify-content-center">
                {/* <h2 className=" mb-2">
                  Submitted Succesfully!
                </h2>
                <p>
                  we will contact you shortly
                </p> */}
                <JumbotronUi />
            </Step>
          </Steps>
          <BottomNavigation
            onClickNext={onClickNext}
            onClickPrev={onClickPrev}
            className="justify-content-center"
            prevLabel="Previous Step"
            nextLabel="Next Step"
            color="secondary"
          />
          
        </Wizard>
      </CardBody>
    </Card>
  );
};
export default injectIntl(Mylogin);
