import React, { useState } from 'react';
// import './App.scss';

function Mylogin() {
 const [userInfo, setUserInfo] = useState({
    email: '',
    firstName: '',
    lastName: '',
    experience: '',
    password: '',
    company: '',
    jobTitle: '',
    location: '',
    profile: ''
 });

 const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
 };

 const handleSubmit = (e) => {
    e.preventDefault();
    console.log(userInfo);
 };

 return (
    <div className="App">
      <h1>Apply as a mentor</h1>
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" placeholder="Email" value={userInfo.email} onChange={handleChange} required />
        <input type="text" name="firstName" placeholder="First Name" value={userInfo.firstName} onChange={handleChange} required />
        <input type="text" name="lastName" placeholder="Last Name" value={userInfo.lastName} onChange={handleChange} required />
        <input type="text" name="experience" placeholder="Experience" value={userInfo.experience} onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" value={userInfo.password} onChange={handleChange} required />
        <input type="text" name="company" placeholder="Company" value={userInfo.company} onChange={handleChange} required />
        <input type="text" name="jobTitle" placeholder="Job Title" value={userInfo.jobTitle} onChange={handleChange} required />
        <input type="text" name="location" placeholder="Location" value={userInfo.location} onChange={handleChange} required />
        <textarea name="profile" placeholder="Profile" value={userInfo.profile} onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </div>
 );
}

export default Mylogin;
